import React, { useState, useEffect } from 'react';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [checkedInEmployees, setCheckedInEmployees] = useState([]);
  const [checkedOutEmployees, setCheckedOutEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
        setFilteredEmployees(data);
        filterEmployees(data); // Filter employees initially
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const filterEmployees = (employeesList) => {
    const checkedIn = employeesList.filter(employee => !employee.exitDate);
    const checkedOut = employeesList.filter(employee => employee.exitDate);
    setCheckedInEmployees(checkedIn);
    setCheckedOutEmployees(checkedOut);
  };

  const handleSearch = () => {
    const filtered = employees.filter(employee =>
      employee.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredEmployees(filtered);
    filterEmployees(filtered);
  };

  const handleExit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const updatedEmployee = await response.json();
        const updatedEmployees = employees.map((employee) =>
          employee.id === id ? updatedEmployee.employee : employee
        );
        setEmployees(updatedEmployees);
        setFilteredEmployees(updatedEmployees);
        filterEmployees(updatedEmployees);
      } else {
        console.error('Error updating employee exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <div>
        <input
          type="text"
          placeholder="Search by name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <h2>Checked In Employees</h2>
      <ul>
        {checkedInEmployees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - {employee.entryDate} - {employee.entryTime} - {employee.exitDate || 'N/A'} - {employee.exitTime || 'N/A'}
            {!employee.exitDate && (
              <button onClick={() => handleExit(employee.id)}>Exit</button>
            )}
          </li>
        ))}
      </ul>
      <h2>Checked Out Employees</h2>
      <ul>
        {checkedOutEmployees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - {employee.entryDate} - {employee.entryTime} - {employee.exitDate} - {employee.exitTime}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
