import React, { useState, useEffect } from 'react';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [checkedInEmployees, setCheckedInEmployees] = useState([]);
  const [checkedOutEmployees, setCheckedOutEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
        setFilteredEmployees(data);
        filterEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const filterEmployees = (employeesList) => {
    const checkedIn = employeesList.filter(employee => !employee.exitDate);
    const checkedOut = employeesList.filter(employee => employee.exitDate);
    setCheckedInEmployees(checkedIn);
    setCheckedOutEmployees(checkedOut);
  };

  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = employees.filter(
      (employee) =>
        employee.name.toLowerCase().includes(query) ||
        employee.position.toLowerCase().includes(query) ||
        employee.department.toLowerCase().includes(query) ||
        employee.email.toLowerCase().includes(query) ||
        employee.phone.toLowerCase().includes(query)
    );
    setFilteredEmployees(filtered);
    filterEmployees(filtered);
  };

  const handleCheckOut = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
      });
      if (response.ok) {
        const updatedEmployee = await response.json();
        const updatedEmployees = employees.map(employee =>
          employee.id === id ? updatedEmployee.employee : employee
        );
        setEmployees(updatedEmployees);
        setFilteredEmployees(updatedEmployees);
        filterEmployees(updatedEmployees);
      } else {
        console.error('Error updating employee exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search employees..."
        value={searchQuery}
        onChange={handleSearch}
        className="form-control mb-3"
      />
      <h3>Checked-In Employees</h3>
      <ul className="list-group mb-4">
        {checkedInEmployees.map((employee) => (
          <li key={employee.id} className="list-group-item d-flex justify-content-between align-items-center">
            {employee.name} - {employee.position} - {employee.department}
            <button onClick={() => handleCheckOut(employee.id)} className="btn btn-secondary">Check Out</button>
          </li>
        ))}
      </ul>
      <h3>Checked-Out Employees</h3>
      <ul className="list-group">
        {checkedOutEmployees.map((employee) => (
          <li key={employee.id} className="list-group-item">
            {employee.name} - {employee.position} - {employee.department}
            <br />
            Checked out at {employee.exitDate} {employee.exitTime}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
