import React, { useState, useEffect } from 'react';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredEmployees, setFilteredEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
        setFilteredEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = () => {
    const filtered = employees.filter(employee =>
      employee.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredEmployees(filtered);
  };

  const handleExit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (response.ok) {
        const updatedEmployee = await response.json();
        setFilteredEmployees(prevEmployees =>
          prevEmployees.map(emp =>
            emp.id === id ? updatedEmployee.employee : emp
          )
        );
      } else {
        console.error('Error updating employee exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <div>
        <input
          type="text"
          placeholder="Search by name"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul>
        {filteredEmployees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - Giriş: {employee.entryDate} {employee.entryTime} - Çıkış: {employee.exitDate ? employee.exitDate : '-'} {employee.exitTime ? employee.exitTime : '-'}
            <button
              onClick={() => handleExit(employee.id)}
              disabled={employee.exitDate !== null}
            >
              Çıkış Yap
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
