import React, { useState, useEffect } from 'react';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleExit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
      });
      if (response.ok) {
        const updatedEmployee = await response.json();
        setEmployees((prevEmployees) =>
          prevEmployees.map((employee) =>
            employee.id === id ? updatedEmployee.employee : employee
          )
        );
      } else {
        console.error('Error updating exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <ul>
        {employees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - Enter: {employee.entryDate} {employee.entryTime} - Exit: {employee.exitDate ? employee.exitDate : '-'} {employee.exitTime ? employee.exitTime : '-'}
            <button
              onClick={() => handleExit(employee.id)}
              disabled={employee.exitDate !== null}
            >
              Çıkış Yap
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
