import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm'; // EmployeeForm bileşenini içe aktarın

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const addEmployee = (employee) => {
    setEmployees((prevEmployees) => [...prevEmployees, employee]);
  };

  const handleExit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const updatedEmployee = await response.json();
        setEmployees((prevEmployees) =>
          prevEmployees.map((emp) =>
            emp.id === id ? { ...emp, ...updatedEmployee.employee } : emp
          )
        );
      } else {
        console.error('Error updating employee exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <EmployeeForm addEmployee={addEmployee} />
      <ul>
        {employees.map((employee, index) => (
          <li key={index}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - {employee.entryDate} - {employee.entryTime} - {employee.exitDate ? `${employee.exitDate} - ${employee.exitTime}` : 'Not exited yet'}
            <button onClick={() => handleExit(employee.id)}>Exit</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
