import React, { useState, useEffect } from 'react';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredEmployees = employees.filter(employee =>
    employee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Employee List</h1>
      <input
        type="text"
        placeholder="Search by name"
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <ul>
        {filteredEmployees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} - Enter: {employee.entryDate} {employee.entryTime} - Exit: {employee.exitDate ? employee.exitDate : '-'} {employee.exitTime ? employee.exitTime : '-'}
            <button
              onClick={() => handleExit(employee.id)}
              disabled={employee.exitDate !== null}
            >
             Exit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
