import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm.js';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/employees');
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleExit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/${id}/exit`, {
        method: 'PUT',
      });

      if (response.ok) {
        const updatedEmployee = await response.json();
        setEmployees((prevEmployees) =>
          prevEmployees.map((emp) => (emp.id === id ? updatedEmployee.employee : emp))
        );
      } else {
        console.error('Error updating employee exit time');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredEmployees = employees.filter(
    (employee) =>
      employee.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const entryEmployees = filteredEmployees.filter(employee => !employee.exitDate && !employee.exitTime);
  const exitEmployees = filteredEmployees.filter(employee => employee.exitDate && employee.exitTime);

  return (
    <div>
      <h1>Employee List</h1>
      <input
        type="text"
        placeholder="Search employees"
        value={searchTerm}
        onChange={handleSearch}
      />
      <h2>Employees with Entry Only</h2>
      <ul>
        {entryEmployees.map((employee, index) => (
          <li key={index}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} -Enter- {employee.entryDate} - {employee.entryTime}
            <button onClick={() => handleExit(employee.id)}>Exit</button>
          </li>
        ))}
      </ul>

      <h2>Employees with Exit</h2>
      <ul>
        {exitEmployees.map((employee, index) => (
          <li key={index}>
            {employee.name} - {employee.email} - {employee.position} - {employee.department} - {employee.phone} -Enter- {employee.entryDate} - {employee.entryTime} -Exit- {employee.exitDate} - {employee.exitTime}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
