import React, { useState, useEffect } from 'react';

const EmployeeForm = ({ addEmployee }) => {
  const [employee, setEmployee] = useState({
    id: '',
    name: '',
    position: '',
    department: '',
    email: '',
    phone: '',
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (employee.name || employee.id) {
      fetchEmployeeData();
    }
  }, [employee.name, employee.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee((prevEmployee) => ({
      ...prevEmployee,
      [name]: value,
    }));
  };

  const fetchEmployeeData = async () => {
    try {
      const response = employee.id
        ? await fetch(`http://localhost:5000/api/employees/${employee.id}`)
        : await fetch(`http://localhost:5000/api/employees/name/${employee.name}`);
      if (response.ok) {
        const data = await response.json();
        setEmployee(data);
      } else {
        console.error('Employee not found');
      }
    } catch (error) {
      console.error('Error fetching employee:', error);
    }
  };

  const validate = () => {
    let formErrors = {};
    if (!employee.name) formErrors.name = "Name is required";
    if (!employee.position) formErrors.position = "Position is required";
    if (!employee.department) formErrors.department = "Department is required";
    if (!employee.email) {
      formErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(employee.email)) {
      formErrors.email = "Email address is invalid";
    }
    if (!employee.phone) {
      formErrors.phone = "Phone number is required";
    } else if (!/^\d{10}$/.test(employee.phone)) {
      formErrors.phone = "Phone number is invalid (should be 10 digits)";
    }
    return formErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formErrors = validate();
    if (Object.keys(formErrors).length === 0) {
      try {
        const response = await fetch('http://localhost:5000/api/employees', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(employee),
        });

        if (response.ok) {
          const newEmployee = await response.json();
          addEmployee(newEmployee.employee);
          setEmployee({
            id: '',
            name: '',
            position: '',
            department: '',
            email: '',
            phone: '',
          });
        } else {
          console.error('Error adding employee');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    } else {
      setErrors(formErrors);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={employee.name}
          onChange={handleChange}
        />
        {errors.name && <p style={{color: 'red'}}>{errors.name}</p>}
      </div>
      <div>
        <input
          type="text"
          name="id"
          placeholder="ID"
          value={employee.id}
          onChange={handleChange}
        />
      </div>
      <div>
        <input
          type="text"
          name="position"
          placeholder="Position"
          value={employee.position}
          onChange={handleChange}
        />
        {errors.position && <p style={{color: 'red'}}>{errors.position}</p>}
      </div>
      <div>
        <input
          type="text"
          name="department"
          placeholder="Department"
          value={employee.department}
          onChange={handleChange}
        />
        {errors.department && <p style={{color: 'red'}}>{errors.department}</p>}
      </div>
      <div>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={employee.email}
          onChange={handleChange}
        />
        {errors.email && <p style={{color: 'red'}}>{errors.email}</p>}
      </div>
      <div>
        <input
          type="tel"
          name="phone"
          placeholder="Phone"
          value={employee.phone}
          onChange={handleChange}
        />
        {errors.phone && <p style={{color: 'red'}}>{errors.phone}</p>}
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default EmployeeForm;
